used_fortunes = {}
user_cooldown = {}
COOLDOWN_SECONDS = 86400  # 24 Ñ‡Ð°ÑÐ°

def get_random_fortune():
    now = time.time()
    for text in list(used_fortunes):
        if now - used_fortunes[text] > 86400:
            del used_fortunes[text]
    available = [f for f in FORTUNES if f not in used_fortunes]
    if not available:
        used_fortunes.clear()
        available = FORTUNES.copy()
    choice = random.choice(available)
    used_fortunes[choice] = now
    return choice

async def inline_query(update: Update, context: ContextTypes.DEFAULT_TYPE) -> None:
    user_id = update.inline_query.from_user.id
    now = time.time()

    if user_id in user_cooldown and now - user_cooldown[user_id] < COOLDOWN_SECONDS:
        warning_text = "Ð£Ñ‚Ð°Ð»Ð¸ ÑÐ²Ð¾Ð¹ Ð¿Ñ‹Ð»! Ð¢Ñ‹ ÑƒÐ¶Ðµ Ð¿Ð¾Ð»ÑƒÑ‡Ð¸Ð» ÑÐ²Ð¾Ñ‘ Ð¿Ñ€ÐµÐ´ÑÐºÐ°Ð·Ð°Ð½Ð¸Ðµ"
        result = InlineQueryResultArticle(
            id=str(uuid4()),
            title="ÐŸÑ‹Ð» Ð¿Ð¾ÑƒÐ±Ð°Ð²ÑŒ...",
            input_message_content=InputTextMessageContent(warning_text),
            description=warning_text,
        )
    else:
        fortune = get_random_fortune()
        result = InlineQueryResultArticle(
            id=str(uuid4()),
            title="Ð¢Ð°ÐºÐ¸Ðµ ÑÐµÐ±Ðµ Ð¿Ñ€Ð¾Ñ€Ð¾Ñ‡ÐµÑÑ‚Ð²Ð°",
            input_message_content=InputTextMessageContent(fortune),
            description=fortune,
        )
        user_cooldown[user_id] = now

    await update.inline_query.answer([result], cache_time=0)

if __name__ == "__main__":
    BOT_TOKEN = os.environ.get("BOT_TOKEN")
    application = Application.builder().token(BOT_TOKEN).build()
    application.add_handler(InlineQueryHandler(inline_query))
    application.run_polling()
